                html += `<img src="${message.avatar}" alt="${message.phone}" class="w-[30px] h-[30px] rounded-full object-cover">`;
            } else {
                html += `<div class="w-[30px]"></div>`;
            }
            
            html += `<div class="rounded-lg px-2.5 py-1 max-w-[75%] sm:max-w-[65%] relative ${isFirstMessage ? 'message-bubble' : ''}" style="background: #171717; ${isFirstMessage ? '--bubble-color: #171717;' : ''}">`;
            
            if (isFirstMessage) {
                html += `<p class="text-[#00A884] text-[13px] font-medium leading-4">${message.phone}</p>`;
            }
            
            if (message.media) {
                html += `<div class="mb-1">`;
                if (message.media.type === "image") {
                    html += `<img src="${message.media.url}" alt="M칤dia" class="rounded-lg media-responsive">`;
                } else if (message.media.type === "video") {
                    html += `<video src="${message.media.url}" controls class="rounded-lg media-responsive"></video>`;
                }
                html += `</div>`;
            }
            
            if (message.content) {
                html += `
                <div class="flex items-end gap-1.5">
                    <p class="text-[#f9f9f9] text-[14.2px] leading-[18px] flex-1">${message.content}</p>
                    <p class="text-[#807f7f] text-[10px] leading-3 flex-shrink-0 self-end mb-0.5">${message.time}</p>
                </div>`;
            } else if (message.time) {
                html += `<p class="text-[#807f7f] text-[10px] leading-3 text-right mt-1">${message.time}</p>`;
            }
            
            html += `</div>`;
            messageElement.innerHTML = html;
            return messageElement;
        }

        function scrollToBottom() {
            const scrollAnchor = document.getElementById('scroll-anchor');
            scrollAnchor.scrollIntoView({ behavior: 'smooth' });
        }

        function showPaymentOverlay() {
            document.getElementById('payment-overlay').classList.remove('hidden');
            document.getElementById('payment-overlay').classList.add('flex');
            
                            document.getElementById('payment-intro').classList.remove('hidden');
                document.getElementById('payment-qr').classList.add('hidden');
                        
            document.body.style.overflow = 'hidden';
            
        }

        function showPaymentQR() {
            document.getElementById('payment-intro').classList.add('hidden');
            document.getElementById('payment-qr').classList.remove('hidden');
        }

        function copyToClipboard(text) {
            const tempInput = document.createElement('textarea');
            tempInput.value = text;
            document.body.appendChild(tempInput);
            tempInput.select();
            document.execCommand('copy');
            document.body.removeChild(tempInput);
            
            const button = document.getElementById('copy-pix-btn');
            button.innerHTML = `
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M20 6 9 17l-5-5"></path></svg>
                Copiado!
            `;
            
            // Pixel Click Pix Copy EXO1
