<!-- CRONOMETRO-->
<script>
  function iniciarContagemRegressiva(duracao, elemento) {
    let tempoRestante = duracao;

    const atualizarCronometro = () => {
      const minutos = Math.floor(tempoRestante / 60);
      const segundos = tempoRestante % 60;
      elemento.textContent = `${String(minutos).padStart(2, '0')}:${String(segundos).padStart(2, '0')}`;

      if (tempoRestante > 0) {
        tempoRestante--;
      } else {
        clearInterval(intervalo);
      }
    };

    atualizarCronometro(); // Atualiza de imediato
    const intervalo = setInterval(atualizarCronometro, 1000);
  }

  document.addEventListener("DOMContentLoaded", function () {
    const cronometroSpan = document.getElementById("cronometro");
    if (cronometroSpan) {
      iniciarContagemRegressiva(15 * 60, cronometroSpan); // 15 minutos
    }
  });
</script>

